import React, {useState, useEffect} from "react";
import '../styles.css';
import MovieCard from "./MovieCard";

export default function MovieGrid(){

    const [movies, setMovies] = useState([]);
    const [searchTerm,setSearchTerm] = useState('');
    const [genre,setGenre] = useState('All Genres');
    const [rating,setRating] = useState('All');

    useEffect(()=>{
        fetch("movies.json")
        .then(res => res.json())
        .then(data => setMovies(data));
    },[]);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value); 
    }
    const handleGenreChange = (e) => {
        setGenre(e.target.value); 
    }
    const handleRatingChange = (e) => {
        setRating(e.target.value); 
    }

    const filteredMovies = movies.filter(movie => 
        movie.title.toLowerCase().includes(searchTerm.toLowerCase()) 
    )

    return(
        <div>
            <input 
                type="text"
                className="search-input"
                placeholder="Enter Movie Name..."
                value={searchTerm}
                onChange={handleSearchChange}
            />
            <div className="filter-bar">
                <div className="filter-slot">
                    <label>Genre</label>
                    <select className="filter-dropdown" value={genre} onChange={handleGenreChange}>
                        <option value="" key="">All Genres</option>
                        <option value="" key="">Action</option>
                        <option value="" key="">Drama</option>
                        <option value="" key="">Horror</option>
                        <option value="" key="">Fantasy</option>
                    </select>
                </div>
                <div className="filter-slot">
                    <label>Rating</label>
                    <select className="filter-dropdown" value={rating} onChange={handleRatingChange}>
                        <option value="" key="">All</option>
                        <option value="" key="">Good</option>
                        <option value="" key="">Ok</option>
                        <option value="" key="">Bad</option>
                    </select>
                </div>
            </div>

            <div className="movies-grid">
                {filteredMovies.map(movie => (
                    <MovieCard movie={movie} key={movie.id}></MovieCard>
                ))}
            </div>
        </div>
    );
}
